    private void jButtonCalcActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonCalcActionPerformed
        // TODO add your handling code here:
        double pp,ef,ep;
        double ponderado;
        pp= Double.parseDouble(jTextFieldPrac.getText());
        ef= Double.parseDouble(jTextFieldEF.getText());
        ep= Double.parseDouble(jTextFieldEP.getText());
        
        ponderado = (pp + 2*ep + 3*ef)/6;
        DecimalFormat f2 = new DecimalFormat("0.00");
        jTextFieldPPond.setText((String)f2.format(ponderado));
        
    }//GEN-LAST:event_jButtonCalcActionPerformed

