    private void jButtonResetActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonResetActionPerformed
        // TODO add your handling code here:
        jTextFieldEF.setText("");
        jTextFieldEP.setText("");
        jTextFieldPrac.setText("");
        jTextFieldPPond.setText("");
        jTextFieldPrac.grabFocus();
    }//GEN-LAST:event_jButtonResetActionPerformed

    private void jTextFieldPracKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jTextFieldPracKeyPressed
        // TODO add your handling code here:
        if (evt.getKeyChar()==KeyEvent.VK_ENTER) {
            jTextFieldEP.grabFocus();
        }
    }//GEN-LAST:event_jTextFieldPracKeyPressed

