    private void jButtonOrdenActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonOrdenActionPerformed
        
        
        double precio=0;
        String message ="Detalles de venta\nTama침o de Pizza\n";
        int resp = 0;
        
  
        if (jRadioButtonPeque.isSelected()) {
            precio+=20;
            message+="Tama침o: Peque침a\n";
        }
        if (jRadioButtonMed.isSelected()) {
            precio+=30;
            message+="Tama침o: Mediana\n";
        }
        if (jRadioButtonGrand.isSelected()) {
            precio+=40;
            message+="Tama침o: Grande\n";
        }
        message+="Tipo de Cubierta\n";
        if (jRadioButtonCDel.isSelected()) {
            precio+=5;
            message+="Cubierta: "+jRadioButtonCDel.getText()+"\n";
        }
        if (jRadioButtonCGru.isSelected()) {
            precio+=7;
            message+="Cubierta: "+jRadioButtonCGru.getText()+"\n";
        }
        message+="Ingredientes EXTRAS\n";
        if (jCheckBoxQuesoEx.isSelected()) {
            precio+=12.5;
            message+=jCheckBoxQuesoEx.getText()+"\n";
        }
        if (jCheckBoxChamp.isSelected()) {
            precio+=20;
            message+=jCheckBoxChamp.getText()+"\n";
        }
        if (jCheckBoxAceitunas.isSelected()) {
            precio+=10;
            message+=jCheckBoxAceitunas.getText()+"\n";
        }
        
        if (jCheckBoxCebolla.isSelected()) {
            precio+=2.5;
            message+=jCheckBoxCebolla.getText()+"\n";
        }
        
        if (jCheckBoxPVerde.isSelected()) {
            precio+=5;
            message+=jCheckBoxPVerde.getText()+"\n";
        }
        
        if (jCheckBoxTomates.isSelected()) {
            precio+=7.5;
            message+=jCheckBoxTomates.getText()+"\n";
        }
        
        message+="Tipo de pedido";
        if (jRadioButtonMesa.isSelected()) {
            precio+=2.5;
            message+="Pedido a mesa.";
        }
        if (jRadioButtonDelivery.isSelected()) {
            precio+=10;
            message+="Pedido Delivery";
        }
        DecimalFormat f2 = new DecimalFormat("0.00");
        resp = JOptionPane.showConfirmDialog(null, message,"Verifique su Orden",JOptionPane.YES_NO_OPTION,JOptionPane.QUESTION_MESSAGE);
        if (resp==JOptionPane.YES_OPTION) {
            JOptionPane.showMessageDialog(null, "Su pizza cuesta: S/."+f2.format(precio),"Boleta de pago",JOptionPane.INFORMATION_MESSAGE);
        }
        
    }//GEN-LAST:event_jButtonOrdenActionPerformed

    private void jButtonNewActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonNewActionPerformed
        buttonGroupCubierta.clearSelection();
        buttonGroupSize.clearSelection();
        buttonGroupPedido.clearSelection();
        jCheckBoxAceitunas.setSelected(false);
        jCheckBoxCebolla.setSelected(false);
        jCheckBoxChamp.setSelected(false);
        jCheckBoxPVerde.setSelected(false);
        jCheckBoxQuesoEx.setSelected(false);
        jCheckBoxTomates.setSelected(false);
        jRadioButtonPeque.grabFocus();
    }//GEN-LAST:event_jButtonNewActionPerformed

    private void jButtonQuitActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonQuitActionPerformed
        int resp;
        resp = JOptionPane.showConfirmDialog(null, "Desea salir de la aplicaci칩n?","Aviso al Cliente",JOptionPane.YES_NO_OPTION,JOptionPane.QUESTION_MESSAGE);
        if (resp==JOptionPane.YES_OPTION) {
            dispose();
        }
        else{
            JOptionPane.showMessageDialog(null, "Sigue en la aplicaci칩n.");
        }      
    }//GEN-LAST:event_jButtonQuitActionPerformed

